import React, { useEffect, useState } from 'react'
import axios from 'axios'
import Pusher from 'pusher-js'


// var pusher = new Pusher('app_key', {
//     cluster: 'cluster',
//     encrypted: true
// })
// const channel = pusher.subscribe('websocket-test')

const pusher = new Pusher(process.env.NEXT_PUBLIC_PUSHER_APP_KEY, {
    cluster: process.env.NEXT_PUBLIC_PUSHER_APP_CLUSTER,
    encrypted: true
});

export default function socket() {
    const [people, setPeople] = useState({ id: null, people: [] });

    const endConnection = () => {
        pusher.disconnect();
        axios.post(`http://localhost:8000/offline/${this.state.id}`);
      }

    useEffect(() => {
        console.log(process.env.NEXT_PUBLIC_PUSHER_APP_KEY)
        pusher = new Pusher(process.env.NEXT_PUBLIC_PUSHER_APP_KEY, {
            cluster: process.env.NEXT_PUBLIC_PUSHER_APP_CLUSTER,
            encrypted: true
        });

        this.channel = this.pusher.subscribe('web-socket');


        axios.get('http://localhost:8000/people').then(({ data }) => {
            const { people = [] } = data;
            setPeople({ people });

            window.onbeforeunload = this.endConnection;

            // Returned function in hook will be only run at end of lifecycle e.g willUnmount
            return function cleanup() {
                endConnection();
            }
        }, []);
    })

    console.log(people)

    return(
        <div>
            <h1>Pusher test</h1>
        </div>
    )



}
