        {msg:"Hi! How are you?", isUser: true, id:123},
        {msg:"Good how are you?", isUser: false, id:123},
        {msg:"Very goood! I have a great job opportunity for you. Interested?", isUser: true, id:123},
        {msg:"Shoot!", isUser: false, id:123},
        {msg:"Lorem ipsum", isUser: true, id:123},
    ]

    const renderMessage = () => {
        return messages.map(e => {
            return (
                <div class={`${!e.isUser ? "col-start-1 col-end-8":"col-start-6 col-end-13"} p-3 rounded-lg`}>
                    <div class="flex items-center justify-start flex-row-reverse">
                        <div
                            class="flex items-center justify-center h-10 w-10 rounded-full bg-indigo-500 flex-shrink-0"
                        >
                            A
          </div>
                        <div
                            class={`relative mr-3 text-sm ${e.isUser ? "bg-indigo-100":"bg-white "} py-2 px-4 shadow rounded-xl`}
                        >
                            <div>
                                Lorem ipsum dolor sit, amet consectetur adipisicing. ?
            </div>
                            <div
                                class="absolute text-xs bottom-0 right-0 -mb-5 mr-2 text-gray-500"
                            >
                                Seen
            </div>
                        </div>
                    </div>
                </div>

            )
        });
    }

    return (
        <div>
            {renderMessage()}
        </div>
    )
}
