import React, { useEffect, useState } from 'react'
import axios from "axios"

import Message from "./Message"

export default function Messages(props) {
    const [messages, setMessages] = useState([])

    useEffect(() => {
        console.log("Messages props ------->",props)
        setMessages(props.messages)

        channel.bind('messageSeen', function (data) {
            const obj = messages.filter(e => e.mid === data.mid)

            console.log("Data and Message", data, messages, obj)
          });
    })

    useEffect(() => {
        const lastMsg = messages.slice(-1)[0]
        console.log("This is the last msg", messages, lastMsg)
        axios.post(`http://localhost:8000/seen/${props.uid}`, { ...lastMsg });
      }, [messages])

    const renderMessage = (oldMsg=[]) => {
        const msg = oldMsg.map((e) => {
            if (e.uid === props.uid) {
                return { ...e, isUser: true };
            } else {
                return { ...e, isUser: false };
            }
        });

        return msg.map((e, i) => {
            return (
                <Message
                    msg={e.msg}
                    showRead={e.showRead}
                    isRead={e.isRead}
                    isUser={e.isUser}
                    mid={e.mid}
                />
            );
        });
    };


    return (
        <div>
            <div class="wd-auto grid grid-cols-12 gap-y-2">{renderMessage(messages)}</div>
        </div>
    )
}
