import React, {useEffect, useState} from 'react'

export default function Message(props) {
    const {msg, showRead, isRead, isUser, mid} = props

    useEffect(()=>{
        console.log("Message rednered", msg, mid)
    })

    return (
        <div
            class={`${isUser ? 'col-start-6 col-end-13' : 'col-start-1 col-end-8'} p-3 rounded-lg`}
        >
            <div
                class={`flex ${!isUser ? 'flex-row items-center' : 'items-center justify-start flex-row-reverse'
                    }`}
            >
                <div class="flex items-center justify-center h-10 w-10 rounded-full bg-indigo-500 flex-shrink-0">
                    A
            </div>
                <div
                    class={`relative text-sm ${isUser ? 'bg-indigo-100 mr-3' : 'bg-white ml-3'
                        } py-2 px-4 shadow rounded-xl`}
                >
                    <div>{msg}</div>
                    {showRead ? (
                        <div class="absolute text-xs bottom-0 right-0 -mb-5 mr-2 text-gray-500">{isRead ? "Read": "Unread"}</div>
                    ) : (
                            ''
                        )}
                </div>
            </div>
        </div>
    );
}
