import Messages from "../components/Message/Messages"

export default function Test(props) {
    let messages = [
        { msg: "Hi! How are you?", isUser: true, id: 123, isRead: true },
        { msg: "Good how are you?", isUser: false, id: 123 },
        { msg: "Very goood! I have a great job opportunity for you. Interested?", isUser: true, id: 123, isRead: true },
        { msg: "Shoot!", isUser: false, id: 123, isRead: false },
        { msg: "Lorem ipsum", isUser: true, id: 123, isRead: true },
    ]

    const uuid ="e456"

    const renderMessage = () => {
        var previousNotUserMsg, index = messages.length - 1;
        for (; index >= 0; index--) {
            if (messages[index].isUser == true) {
                previousNotUserMsg = index;
                break;
            }
        }

        messages[previousNotUserMsg].showRead = true

        return messages.map((e, i) => {


            return (
                <div class="wd-auto grid grid-cols-12 gap-y-2">
                    <Messages
                        messages={messages}
                        uid={uid}
                    ></Messages>
                </div>

            )
        });
    }

    return (
        <div >
            {renderMessage()}
        </div>
    )
}
