import React, { useEffect, useState } from 'react'
import axios from 'axios'
import Pusher from 'pusher-js'


// var pusher = new Pusher('app_key', {
//     cluster: 'cluster',
//     encrypted: true
// })
// const channel = pusher.subscribe('websocket-test')

let pusher = new Pusher(process.env.NEXT_PUBLIC_PUSHER_APP_KEY, {
    cluster: process.env.NEXT_PUBLIC_PUSHER_APP_CLUSTER,
    encrypted: true
});

export default function socket() {
    const [people, setPeople] = useState({ id: null, people: [] });

    const endConnection = () => {
        pusher.disconnect();
        axios.post(`http://localhost:8000/offline/${people.id}`);
    }

    useEffect(() => {
        console.log(process.env.NEXT_PUBLIC_PUSHER_APP_KEY)
        // let pusher = new Pusher(process.env.NEXT_PUBLIC_PUSHER_APP_KEY, {
        //     cluster: process.env.NEXT_PUBLIC_PUSHER_APP_CLUSTER,
        //     encrypted: true
        // });

        let channel = pusher.subscribe('websocket-test');

        channel.bind('location-update', function (data) {

        console.log('Received my-event with message: ' + data.message);

        });

        axios.get('http://localhost:8000/people').then(({ data }) => {
            const { people = [] } = data;
            setPeople({ people });

            window.onbeforeunload = endConnection;

            const positionUpdate = setInterval(() => {
                let lat = 1111 + Math.random() * 0.001;
                let lng = 2222 + Math.random() * 0.001;

                axios.post(`http://localhost:8000/transit/${"bb91a987-f90e-4ea4-b5d6-32d079fc1a4a"}`, { lat, lng });
                console.log(`/transit/${"bb91a987-f90e-4ea4-b5d6-32d079fc1a4a"}`, { lat, lng })
              }, 3000);

            // Returned function in hook will be only run at end of lifecycle e.g willUnmount
            return function cleanup() {
                endConnection();
                cleanInterval(positionUpdate)
            }
        }, []);

    }, [])


    console.log(people)
    return (
        <div>
            <h1>Pusher test</h1>
            {people.people.map(e => (
                <div>
                    <div>{e.name}</div>
                    <div>{e.position.lat}</div>
                    <div>{e.position.lng}</div>
                </div>

            ))}
        </div>
    )



}
