// Next.js API route support: https://nextjs.org/docs/api-routes/introduction

export default (req, res) => {
  res.statusCode = 200
  console.log()
  const {email, password} = JSON.parse(req.body)
try{
  const data = checkAuth(email, password)
  res.json(data)
} catch(e){
  res.status(400).json(e)
}


}


const checkAuth = (email, pw) => {
  if(email == "test@test.com" && pw == "123"){
    return {auth: true}
  } else{
    throw {message: "Username or password incorrect", auth: false}
  }
}
